import React, { useEffect, useRef, useState } from 'react';
import Logo from './Logo';
import './GamePage.module.css';
import styles from './GamePage.module.css';
import OthelloBoard from './Canvas-test';

function GamePage() {
    const canvasRef = useRef(null);
    const [started, setStarted] = useState(0); // started is the variable, 0 is what it's initialized w/
    // const [turn, setTurn] = useState("W");
    const [userId, setUserId] = useState(localStorage.getItem("userid"));
    const [guestId, setGuestId] = useState(localStorage.getItem("guestid"));

    // runs as the page loads; happens as soons as this component renders; NEVER mutate the state here (modify the state manually)
    // react state can only be changed with its setter function
    useEffect(() => { 

        // function obtainScreenInformation() {
        //     const mobile = canvas.height > canvas.width;
        // }

        // obtainScreenInformation();
        // window.addEventListener("orientationchange", obtainScreenInformation, false);
        //const interval = setInterval(draw, 1000 / 50); // 50 fps

        //return () => clearInterval(interval);
    }, []);
    // dependency array; code within useEffect will get updated every time the variables inside the array changes 

    const handleConnectClick = () => {
        let gameId;
        if (!userId) {
            if (!guestId) {
                guestId = crypto.randomUUID();
                localStorage.setItem("guestid", guestId);
            } else {
                gameId = guestId;
            }
        } else {
            gameId = userId;
        }

        setStarted(1);

        const newWs = new WebSocket("ws://localhost:8000/othelloml_api/ws/pvp-session/" + gameId);
        setWs(newWs);

        newWs.onmessage = (event) => {
            const msg = JSON.parse(event.data);
            console.log(msg);
        };

        newWs.onopen = (event) => {
            newWs.send("d");
        };

        newWs.onclose = (event) => {
            alert("Connection lost! This might be intentional.");
        };

        newWs.onerror = (event) => {
            alert("Error occurred!");
        };
    };

    return (
        <div className="container py-5">
            <div className={styles.header}>
                <Logo alt="Logo" className={styles.logo} />
                <h1 style={{ fontSize: '60px' }}>Othello ML</h1>
            </div>
            <div className={styles.main}>
                <div className={styles.nav}>
                    <a href="#" id={styles.home}>Home</a>
                    <a href="#" className={styles.button} id={styles.newGameBtn}>Game</a>
                    <a href="#">Rules</a>
                    <a href="#">About</a>
                    <input type="text" placeholder="search player" id={styles.search} />
                </div>
                <div className={styles.game}>
                    <div className="text-center ms-3">
                        {/* <canvas ref={canvasRef} onClick={handleCanvasClick} id="oth" width="602" height="602"></canvas> */}
                        <OthelloBoard started={started}/>
                    </div>
                    <div className={styles.gameText}>
                        <div>
                            <div className={styles.count}>
                                <h2 id={styles.numWhite}>White: 1</h2>
                                <h2 id={styles.numBlack}>Black: 1</h2>
                            </div>
                        </div>
                        <div>
                            <button id={styles.connectBtn} onClick={handleConnectClick}>Connect</button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default GamePage;
