import React, { useEffect, useRef, useState } from 'react';
import Logo from './Logo';
import './GamePage.module.css';
import styles from './GamePage.module.css';
import CanvasComponent from './Canvas-test';

function GamePage() {
    const canvasRef = useRef(null);
    // const [started, setStarted] = useState(0);
    const [board, setBoard] = useState([]);
    const [whiteCount, setWhiteCount] = useState(2);
    const [blackCount, setBlackCount] = useState(2);
    const [turn, setTurn] = useState("W");
    const [ws, setWs] = useState(null);
    const [userId, setUserId] = useState(localStorage.getItem("userid"));
    const [guestId, setGuestId] = useState(localStorage.getItem("guestid"));

    const started = 0;
    const b = 2; // Padding between cells
    const gridsize = 75;

    useEffect(() => {
        const canvas = canvasRef.current;
        const ctx = canvas && canvas.getContext("2d");
        const cw = canvas && canvas.width;
        const ch = canvas && canvas.height;

        if (ctx) {
            ctx.mozImageSmoothingEnabled = false;
            ctx.imageSmoothingEnabled = false;
        }

        function obtainScreenInformation() {
            const mobile = canvas.height > canvas.width;
        }

        function init() {
            const initialBoard = Array(8).fill("").map(() => Array(8).fill(""));
            initialBoard[3][3] = "W";
            initialBoard[4][3] = "B";
            initialBoard[4][4] = "W";
            initialBoard[3][4] = "B";
            setBoard(initialBoard);
            setWhiteCount(2);
            setBlackCount(2);
        }

        function draw() {
            // Without this, previous frames remain on the screen
            ctx.clearRect(0, 0, cw, ch);

            // Background
            ctx.fillStyle = "green";
            ctx.fillRect(0, 0, cw, ch);

            ctx.fillStyle = "black";

            // Draw the lines
            for (var d1 = 0; d1 < 602; d1 += gridsize) {
                ctx.fillRect(d1, 0, b, ch);
                ctx.fillRect(0, d1, cw, b);
            }

            // Draw the 4 weird little circles around the middle 4 squares:
            ctx.beginPath();
            ctx.arc(150 + b / 2, 150 + b / 2, 3 * b, 0, 3 * Math.PI);
            ctx.fill();
            ctx.closePath();

            ctx.beginPath();
            ctx.arc(150 + b / 2, 452 - b / 2, 3 * b, 0, 3 * Math.PI);
            ctx.fill();
            ctx.closePath();
            ctx.beginPath();

            ctx.arc(452 - b / 2, 150 + b / 2, 3 * b, 0, 3 * Math.PI);
            ctx.fill();
            ctx.closePath();

            ctx.beginPath();
            ctx.arc(452 - b / 2, 452 - b / 2, 3 * b, 0, 3 * Math.PI);
            ctx.fill();
            ctx.closePath();

            // Change the content of the piece-counters
            // numWhiteElement.textContent = "white:  " + whiteCount;
            // numBlackElement.textContent = "black:  " + blackCount;

            // Draw the pieces
            for (let x = 0; x < 8; x++) {
                for (let y = 0; y < 8; y++) {
                    if (board[x][y] == "W") {
                        ctx.beginPath();
                        ctx.arc( // define the circle
                            x * gridsize + gridsize / 2,
                            y * gridsize + gridsize / 2,
                            gridsize / 2 - 4 * b, // radius
                            0, // startAngle
                            2 * Math.PI // endAngle
                        );
                        ctx.fillStyle = "white";
                        ctx.fill();
                        ctx.closePath();
                    } else if (board[x][y] == "B") {
                        ctx.beginPath();
                        ctx.arc(
                            x * gridsize + gridsize / 2,
                            y * gridsize + gridsize / 2,
                            gridsize / 2 - 4 * b,
                            0,
                            2 * Math.PI
                        );
                        ctx.fillStyle = "black";
                        ctx.fill();
                        ctx.closePath();
                    }
                }
            }

            // Start menu code (likely to change over time!)
            if (started == 0) {
                ctx.fillStyle = "#fffa";
                ctx.fillRect(0, 0, cw, ch);

                ctx.font = "30px arial";
                ctx.fillStyle = "#000";
                ctx.textAlign = "center";
                ctx.fillText("Click the Connect button to start the game.", cw / 2, 100);
            }
        }

        obtainScreenInformation();
        window.addEventListener("orientationchange", obtainScreenInformation, false);
        init();
        const interval = setInterval(draw, 1000 / 50); // 50 fps

        return () => clearInterval(interval);
    }, [board, whiteCount, blackCount]);

    const handleCanvasClick = (event) => {
        const canvas = canvasRef.current;
        const boundingRect = canvas.getBoundingClientRect();
        const x = event.clientX - boundingRect.left;
        const y = event.clientY - boundingRect.top;

        if (started === 0) return;

        const targetX = Math.floor(x / gridsize);
        const targetY = Math.floor(y / gridsize);

        if (board[targetX][targetY] !== "") return;

        const newBoard = [...board];
        newBoard[targetX][targetY] = turn;
        setBoard(newBoard);

        const newTurn = turn === "W" ? "B" : "W";
        setTurn(newTurn);

        if (newTurn === "W") {
            setWhiteCount(prevCount => prevCount + 1);
        } else {
            setBlackCount(prevCount => prevCount + 1);
        }
    };

    const handleConnectClick = () => {

        let gameId;
        if (!userId) {
            if (!guestId) {
                guestId = crypto.randomUUID();
                localStorage.setItem("guestid", guestId);
            } else {
                gameId = guestId;
            }
        } else {
            gameId = userId;
        }

        started = 1;

        const newWs = new WebSocket("ws://localhost:8000/othelloml_api/ws/pvp-session/" + gameId);
        setWs(newWs);

        newWs.onmessage = (event) => {
            const msg = JSON.parse(event.data);
            console.log(msg);
        };

        newWs.onopen = (event) => {
            newWs.send("d");
        };

        newWs.onclose = (event) => {
            alert("Connection lost! This might be intentional.");
        };

        newWs.onerror = (event) => {
            alert("Error occurred!");
        };
    };

    return (
        <div className="container py5">
            <CanvasComponent/>
            <div className={styles.header}>
                <Logo alt="Logo" className={styles.logo} />
                <h1 style={{ fontSize: '60px' }}>Othello ML</h1>
            </div>
            <div className={styles.main}>
                <div className={styles.nav}>
                    <a href="#" id={styles.home}>Home</a>
                    <a href="#" className={styles.button} id={styles.newGameBtn}>Game</a>
                    <a href="#">Rules</a>
                    <a href="#">About</a>
                    <input type="text" placeholder="search player" id={styles.search} />
                </div>
                <div className={styles.game}>
                    <div className="text-center ms-3">
                        <canvas ref={canvasRef} onClick={handleCanvasClick} id={styles.oth} width="602" height="602"></canvas>
                    </div>
                    <div className={styles.gameText}>
                        <div>
                            <div className={styles.count}>
                                <h2 id={styles.numWhite}>White: {whiteCount}</h2>
                                <h2 id={styles.numBlack}>Black: {blackCount}</h2>
                            </div>
                        </div>
                        <div>
                            <button id={styles.connectBtn} onClick={handleConnectClick}>Connect</button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default GamePage;
