import React, { useEffect, useRef, useState } from "react";

function OthelloBoard(started) {
  const canvasRef = useRef(null);
  const [board, setBoard] = useState([]);
  const [whiteCount, setWhiteCount] = useState(2);
  const [blackCount, setBlackCount] = useState(2);
  //const [started, setStarted] = useState(0); // started is the variable, 0 is what it's initialized w/
  const [turn, setTurn] = useState("W");
  const gridsize = 75;

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    // Draw a red rectangle
    var cw = canvas.width;
    var ch = canvas.height;
    var b = 2; // Padding between cells
    var fps = 50;

    var board = [];
    var whiteCount = 0;
    var blackCount = 0;
    var paused = 0; // 1: paused; 0: not paused
    var started = 0; // 1: started; 0: not started

    function init() {
      paused = 0;
      board = [];

      for (let i = 0; i < 8; i++) {
        board[i] = ["", "", "", "", "", "", "", ""];
      }

      board[3][3] = "W";
      board[4][3] = "B";
      board[4][4] = "W";
      board[3][4] = "B";

      whiteCount = 2;
      blackCount = 2;
    }

    init();
    setInterval(draw, 1000 / fps);

    /* **************** */
    /*      DRAW        */
    /* **************** */

    // This runs every frame and handles all rendering drawing operations.
    function draw() {
      // Without this, previous frames remain on the screen
      ctx.clearRect(0, 0, cw, ch);

      // Background
      ctx.fillStyle = "green";
      ctx.fillRect(0, 0, cw, ch);

      ctx.fillStyle = "black";

      // Draw the lines
      for (var d1 = 0; d1 < 602; d1 += gridsize) {
        ctx.fillRect(d1, 0, b, ch);
        ctx.fillRect(0, d1, cw, b);
      }

      // Draw the 4 weird little circles around the middle 4 squares:
      ctx.beginPath();
      ctx.arc(150 + b / 2, 150 + b / 2, 3 * b, 0, 3 * Math.PI);
      ctx.fill();
      ctx.closePath();

      ctx.beginPath();
      ctx.arc(150 + b / 2, 452 - b / 2, 3 * b, 0, 3 * Math.PI);
      ctx.fill();
      ctx.closePath();
      ctx.beginPath();

      ctx.arc(452 - b / 2, 150 + b / 2, 3 * b, 0, 3 * Math.PI);
      ctx.fill();
      ctx.closePath();

      ctx.beginPath();
      ctx.arc(452 - b / 2, 452 - b / 2, 3 * b, 0, 3 * Math.PI);
      ctx.fill();
      ctx.closePath();

      // Change the content of the piece-counters
      //numWhiteElement.textContent = "white:  " + whiteCount;
      //numBlackElement.textContent = "black:  " + blackCount;

      // Draw the pieces
      for (let x = 0; x < 8; x++) {
        for (let y = 0; y < 8; y++) {
          if (board[x][y] == "W") {
            ctx.beginPath();
            ctx.arc(
              // define the circle
              x * gridsize + gridsize / 2,
              y * gridsize + gridsize / 2,
              gridsize / 2 - 4 * b, // radius
              0, // startAngle
              2 * Math.PI // endAngle
            );
            ctx.fillStyle = "white";
            ctx.fill();
            ctx.closePath();
          } else if (board[x][y] == "B") {
            ctx.beginPath();
            ctx.arc(
              x * gridsize + gridsize / 2,
              y * gridsize + gridsize / 2,
              gridsize / 2 - 4 * b,
              0,
              2 * Math.PI
            );
            ctx.fillStyle = "black";
            ctx.fill();
            ctx.closePath();
          }
        }
      }
    }
  }, [board, whiteCount, blackCount]);

  const handleCanvasClick = (event) => {
    const canvas = canvasRef.current;
    const boundingRect = canvas.getBoundingClientRect();
    const x = event.clientX - boundingRect.left;
    const y = event.clientY - boundingRect.top;

    if (started === 0) return;

    const targetX = Math.floor(x / gridsize);
    const targetY = Math.floor(y / gridsize);

    if (board[targetX][targetY] !== "") return;

    const newBoard = [...board];
    newBoard[targetX][targetY] = turn;
    setBoard(newBoard);

    const newTurn = turn === "W" ? "B" : "W";
    setTurn(newTurn);

    if (newTurn === "W") {
      setWhiteCount((prevCount) => prevCount + 1);
    } else {
      setBlackCount((prevCount) => prevCount + 1);
    }
  };

  return <canvas ref={canvasRef} width={602} height={602} />;
}

export default OthelloBoard;
